import React, { useEffect, useState } from 'react'; 
import axios from 'axios'; 
import Popup from "reactjs-popup";
import UpdateProfileForm from '../components/UpdateProfileForm.js';

function UserProfileComponent({ updateProfileFunction }){
    const [userData, setUserData] = useState({});
    useEffect(() => {
        axios  
            .get(
             //local:
             `http://localhost:4000/`
             //production: 
            //  `https://myheroku-deployed-api.heroku.com`
            )
            .then(function(response){
                console.log("response", response.data)
                setAllUserData(response.data);
            })
            .catch(function(error){
                console.log(error);
            });
    }, []);


    return (
    <div className="Wrapper CreateForm">
         <p>First Name: {userData.firstName}</p>
           <Popup trigger={<button className="btn"> Update Profile</button>} modal closeOnDocumentClick>
           <UpdateProfileForm updateProfileFunction={updateProfileFunction}/>
           </Popup>

    </div>
    );
}

export default UserProfileComponent;
