import React, { useEffect, useState } from 'react'; 
import axios from 'axios'; 
import Popup from "reactjs-popup";
import UpdateProfileForm from '../components/UpdateProfileForm.js';

function UserProfileComponent({ updateProfileFunction, userInformation }){
    const [UserData, setUserData]=useState([]);
    const [FirstName, setFirstName] = ("");
    const uid = userInformation.uid;

    useEffect(() => {

        console.log(UserData);
        axios  
            .get(
             //local:
             `http://localhost:4000/userprofile`
             //production: 
            //  `https://myheroku-deployed-api.heroku.com`
            )
            .then(function(response){
                console.log("response", response.data)
                setUserData(response.data);
            })
            .catch(function(error){
                console.log(error);
            });
    }, []);


    useEffect(()=> {
        console.log('UserData', UserData);
          if(UserData.hits){
            // setFirstName(UserData[0].);
            setFirstName(UserData[0].firstName);

          }
      }, [UserData])
      console.log("firstName",FirstName);
    return (

        <div className="CreateForm Wrapper">
        <h2>Post</h2>
        <p>{FirstName}</p>
          <Popup trigger={<button className="btn"> Update Profile</button>} modal closeOnDocumentClick>
          <UpdateProfileForm updateProfileFunction={updateProfileFunction}/>
          </Popup>
         </div>

    )
}

export default UserProfileComponent;
